glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in vec2 a_uv0;
in vec4 a_color;
out vec2 v_uv0;
out vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;
in vec2 v_uv0;
in vec4 v_color;
uniform sampler2D texture;
uniform Properties {
  vec2 size;
};
const float RADIUS = 5.0;
vec4 getBlurColor (vec2 uv) {
  vec4 color = vec4(0);
  float sum = 0.0;
  for (float r = -RADIUS; r <= RADIUS; r++) {
    float x = uv.x + r / size.x;
    if (x < 0.0 || x > 1.0) continue;
    for (float c = -RADIUS; c <= RADIUS; c++) {
      float y = uv.y + c / size.y;
      if (y < 0.0 || y > 1.0) continue;
      vec2 target = vec2(x, y);
      float weight = (RADIUS - abs(r)) * (RADIUS - abs(c));
      color += texture2D(texture, target) * weight;
      sum += weight;
    }
  }
  color /= sum;
  return color;
}
void main () {
  vec4 color = v_color;
  color *= texture(texture, v_uv0);
  if (color.a != 0.0) {
    color = getBlurColor(v_uv0);
  }
  color.a *= v_color.a;
  gl_FragColor = color;
}

